import React, { Component } from "react";
import { connect } from "react-redux";
import Table from "@material-ui/core/Table";

import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { getBanks } from "../Saga";
import InputLabel from "@material-ui/core/InputLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import NativeSelect from "@material-ui/core/NativeSelect";

class BankTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      banks: [],
      pagination: 10,
    };
  }

  componentDidMount() {
    this.props.dispatch(getBanks());
  }

  componentWillReceiveProps(props) {
    if (props.banks !== null) {
      this.setState({
        banks: props.banks,
      });
    }
  }

  render() {
    return (
      <>
        <Paper
          style={{ marginRight: "10%", marginLeft: "10%", marginTop: "5%" }}
        >
          Banks
          <TableContainer>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Id</TableCell>
                  <TableCell align="center">Bank</TableCell>
                  <TableCell align="center">IFSC</TableCell>
                  <TableCell align="center">Branch </TableCell>
                  <TableCell align="center">Bank ID</TableCell>
                  <TableCell align="center">Address</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {this.state.banks
                  .slice(0, this.state.pagination)
                  .map((bank, index) => (
                    <TableRow>
                      <TableCell component="th" scope="row">
                        {bank.id}
                      </TableCell>
                      <TableCell align="center">{bank.bank_name}</TableCell>
                      <TableCell align="center">{bank.ifsc}</TableCell>
                      <TableCell align="center">{bank.branch}</TableCell>
                      <TableCell align="center">{bank.bank_id}</TableCell>
                      <TableCell align="center">{bank.address}</TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>
          <div align="right">
            <div
              style={{
                display: "flex",
                flex: 1,
                flexDirection: "row",
                justifyContent: "flex-end",
              }}
            >
              <div
                style={{
                  margin: "1%",
                }}
              >
                Rows per page
                <FormControl>
                <NativeSelect
                  value={this.state.pagination}
                  style={{ marginLeft: "1%" }}
                  // onChange={handleChange}
                  inputProps={{
                    name: "age",
                    id: "age-native-helper",
                  }}
                >
                  <option aria-label="None" value="" />
                  <option value={10}>10</option>
                  <option value={20}>20</option>
                </NativeSelect>
                {/* <FormHelperText>Some important helper text</FormHelperText> */}
                </FormControl>
              </div>
              <div style={{ margin: "1%" }}>
                {" "}
                1-{this.state.pagination} of {this.state.banks.length} {`< >`}
              </div>
            </div>
          </div>
        </Paper>
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    banks: state.reducer.banks,
  };
};

export default connect(mapStateToProps)(BankTable);
